(79, 'E’ZOZ-EHTIROMGA LOYIQ KEKSALAR...', 'ezoz-ehtiromga-loyiq-keksalar', '<p style=\"text-align: center;\">\"Tank rota komandiri\"</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Pedagogika fakulteti Boshlang‘ich ta’lim va sport tarbiyaviy ishi 18/3 guruhi talabalari guruh rahbari boshchiligida Fashizmga qarshi kurashda mardonovor jang olib borgan urush qatnashchisi Kolosov Grigoriy Alekseyevichni yaqinlashayotgan ayyom bilan qutlashdi. Otaxon 1926-yilda tavallud topgan ekanlar. 17 yoshida urushga ketib, 1944-1947-yillar oralig‘ida oddiy askarlikdan to \"Tank rota komandiri\" vazifalarida faoliyat yuritgan. Bugungi tinch va osoyishta kunlarimiz ortida mana shunday insonlarning sharafli va zahmatli mehnatlari yotganligini hech qachon unutmaylik!</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/photo_2019-05-04_12-39-45.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/photo_2019-05-04_12-39-56.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-04 02:41:06', '1', 1246, '2019-05-04 02:40:38', '2021-01-06 12:17:38', '/photo_2019-05-04_12-39-45.jpg', 2, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(80, 'Biz huquqbuzarlik, jinoyatchilik, diniy ekstremizm va giyohvandlikka qarshimiz!', 'biz-huquqbuzarlik-jinoyatchilik-diniy-ekstremizm-va-giyohvandlikka-qarshimiz', '<p style=\"text-align: justify;\">2019-yil 2-may kuni <strong>“Huquqbuzarlik, jinoyatchilik, diniy ekstremizm va giyohvandlikka qarshimiz” </strong>mavzusida davra suhbati tashkil etildi</p>', '<p align=\"center\" style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Pedagogika fakulteti tomonidan 2019-yil 2-may kuni <strong>“Huquqbuzarlik, jinoyatchilik, diniy ekstremizm va giyohvandlikka qarshimiz” </strong>mavzusida davra suhbati tashkil etildi.Tadbirda Chirchiq shaxar IIB, shahar prokurorturasi xodimlari va  bosh imom xatib о‘z ma’ruzalari bilan ishtirok etishdi. Tadbir davomida fakultetning “Musiqa ta’limi” yо‘nalishi talabalari tomonidan Ismoil Shomurodovning asari asosida sahnalashtirilgan “Ortepa” drammasi namoyish etildi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7675-min.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7719-min.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7670-min.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7634-min.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7610-min.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7546-min.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p>\r\n	<br>\r\n</p>', '2019-05-04 04:19:56', '1', 1550, '2019-05-04 04:19:56', '2021-01-07 03:57:48', '/2019- yilgi/05.2019 may/IMG_7693-min.jpg', 1, NULL, 13, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(81, 'PREZIDENT TASHABBUSI IJRODA', 'prezident-tashabbusi-ijroda', '<p style=\"text-align: justify;\">Talabalar turar joyi talabalari ishtirokida fakultetlararo “Yosh kitobxon” tanlovi bо‘lib о‘tdi.</p>', '<p style=\"text-align: justify;\">Talabalar turar joyi talabalari ishtirokida fakultetlararo “Yosh kitobxon” tanlovi bо‘lib о‘tdi. Tanlovda Aniq va tabiiy fanlar fakulteti talabalari 18 ball bilan 3-о‘rinni, Pedagogika fakulteti talabalari 22 ball bilan 2-о‘rinni, Tarix va tillar fakulteti talabalari 32 ball bilan 1-о‘rinni egalladilar.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/Talabalar%20turar%20joyi%20talabalari2.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/Talabalar%20turar%20joyi%20talabalari3.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/Talabalar%20turar%20joyi%20talabalari4.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>', '2019-05-06 03:49:39', '1', 1459, '2019-05-06 03:49:39', '2021-01-06 23:48:23', '/2019- yilgi/05.2019 may/Talabalar turar joyi talabalari.jpg', 1, NULL, 5, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(82, 'KEKSALAR DOIM E`ZOZDA', 'keksalar-doim-ezozda', '<p>Toshkent Viloyati Chirchiq Davlat Pedagogika instituti Pedagogika fakulteti \"Maktabgacha va boshlang\'ich ta\'lim sport tarbiyaviy ishi\"kafedrasi</p>', '<p align=\"center\" style=\"text-align: justify;\">Toshkent Viloyati Chirchiq Davlat Pedagogika instituti Pedagogika fakulteti \"Maktabgacha va boshlang\'ich ta\'lim sport tarbiyaviy ishi\"kafedrasi Maktabgacha ta\'lim yo\'nalishi talabalari Bo\'stonliq tuman  Qo\'shqo\'rg\'on qishlog\'ida yashovchi 100 yoshni qarshi olgan Ergashboy ota To\'laganov hamda Parkent tumani Oybek mahallasida yashovchi II jahon urushi qatnashchisi 1925-yil tavallud topgan Tòxta ota Xojiyevlar xonadonlariga tashrif buyurdilar! Uchrashuvda keksalarimiz institut talaba yoshlarining kelajakda o`z sohasini yetuk mutahassislari bo`lishini va  yurtimiz tinchligini tilab duo qildilar!</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/keksalar-doim-ezozda2.png\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/keksalar-doim-ezozda3.png\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/keksalar-doim-ezozda4.png\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-05-06 04:59:16', '1', 1211, '2019-05-06 04:59:16', '2021-01-06 17:19:30', '/2019- yilgi/05.2019 may/keksalar-doim-ezozda.png', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(83, 'Nafosat malikalari', 'nafosat-malikalari', '<p style=\"text-align: justify;\">О‘zbekiston Ruspublikasi Prezidentining 2018 yil 2 fevraldagi “Xotin-qizlarni qо‘llab-quvvatlash va oila institutini mustahkamlash sohasidagi faoliyatni tubdan takomillashtirish</p>', '<p align=\"center\" style=\"text-align: justify;\">О‘zbekiston Ruspublikasi Prezidentining 2018 yil 2 fevraldagi “Xotin-qizlarni qо‘llab-quvvatlash va oila institutini mustahkamlash sohasidagi faoliyatni tubdan takomillashtirish chora-tadbirlari tо‘g‘risida”gi PF-5325-son Farmonining ijrosini ta’minlash maqsadida institutimiz talaba qizlari о‘rtasida “Nafosat malikalari” kо‘rik tanlovi о‘tkazildi. Tanlovda “Tarix va tillar”,“Aniq va tabiiy”,”Pedagogika” fakultetlarida faol qizlar ishtirok etishdi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7206.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7177.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7115.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>', '2019-05-06 05:08:39', '1', 1301, '2019-05-06 05:08:39', '2021-01-06 18:05:46', '/2019- yilgi/05.2019 may/IMG_7082.jpg', 1, NULL, 5, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(84, 'Tarix va tillar fakultetida ota-onalar bilan suhbat', 'tarix-va-tillar-fakultetida-ota-onalar-bilan-suhbat', '<p style=\"text-align: justify;\">Tarix va tillar fakultetida Xorijiy til va adabiyoti yо‘nalishi talabalarining ota-onalari bilan suhbat о‘tkazildi</p>', '<p style=\"text-align: justify;\">2019 yil 4-may kuni Chirchiq davlat pedagogika instituti Tarix va tillar fakultetida Xorijiy til va adabiyoti yо‘nalishi talabalarining ota-onalari bilan suhbat о‘tkazildi. Fakultet dekani R.A.Ikramov boshchiligida о‘tkazilgan yig‘ilishda har bir guruh murabbiylari va Xorijiy tillar kafedrasi mudiri ushbu yо‘nalishda о‘qiyotgan talabalar yutuq va kamchiliklari, kafedraning istiqbol rejalari haqida sо‘z yuritishdi hamda ota-onalar fikri tinglandi. Namunaviy hulqi va a’lo baholari bilan о‘rnak bо‘lib kelayotgan talabalar ota-onalariga tashakkurnomalar topshirildi. Suhbat sо‘nggida ota-onalar farzandlari о‘qiyotgan oliygoh haqida iliq fiklar bilidirishdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/IMG_2160.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/IMG_2172.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/uploaded-files/20190504_153006.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-06 08:35:50', '1', 1220, '2019-05-06 08:35:50', '2020-12-31 13:19:03', '/2019- yilgi/05.2019 may/IMG_2158.jpg', 2, NULL, 13, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(85, 'OLIMLARIMIZ TALABALAR DAVRASIDA', 'olimlarmiz-elimiz-ardogida', '<p style=\"text-align: justify;\">“Olimlardan-mahorat darslari” mavzusida uchrashuv tashkil etildi</p>', '<p style=\"text-align: justify;\">О‘zbekiston Respublikasi Xavfsizlik kengashining 2019 yil 11 yanvarida tasdiqlangan “Yо‘l xaritasi” ijrosi yuzasidan о‘tkazilgan Ishchi guruh yig‘ilishining 1-son bayonida belgilangan vazifalar ijrosi yuzasidan 2019 yil 4 may kuni Toshkent viloyati Chirchiq davlat pedagogika instituti Pedagogika fakulteti “Maktabgacha va boshlang‘ich ta’lim sporti” kafedrasi tomonidan “Olimlardan-mahorat darslari” mavzusida uchrashuv tashkil etildi. Uchrashuvda pedagogika fakulteti dekani p.f.d., prof Mardonov Shukurullo Qо‘ldoshevich, Qarshi davlat universiteti p.f.d., prof. Choriyev Abdushukur Choriyevich, Nizomiy nomidagi TDPU professori Jumayev Mamanazar Ergashevich hamda shu yо‘nalish talabalari ishtirok etishdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/Suhbat2.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/Suhbat3.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/Suhbat4.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-06 08:51:12', '1', 1183, '2019-05-06 08:50:13', '2020-12-28 04:27:59', '/2019- yilgi/05.2019 may/Suhbat1.jpg', 2, NULL, 9, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(86, '“QUVNOQ STARTLAR” BELLASHUVI BО‘KA TUMANIDA …', 'quvnoq-startlar-bellashuvi-boka-tumanida', '<p style=\"text-align: justify;\"><strong>“Bо‘sh vaqtim bilim va sport bilan birga”</strong></p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Maktabgacha va boshlang‘ich ta’lim sporti kafedrasi, “Jismoniy madaniyat” kafedrasi &nbsp;tomonidan Bо‘ka tumani jismoniy tarbiya va sport bо‘limi, tuman xalq ta’limi bо‘limi bilan hamkorlikda hududdagi о‘rta ta’lim maktablarining 7-sinf о‘quvchilari о‘rtasida <strong>“Bо‘sh vaqtim bilim va sport bilan birga”</strong> shiori ostida “Quvnoq startlar” sport musobaqasi tashkil etildi. Musobaqada tumanning 5 ta maktab о‘quvchi jamoalari &nbsp;ishtirok etishib musobaqalarda bellashishdi. Musobaqa sо‘ngida g‘olib jamoalar tashkilotchilar tomonidan maxsus diplomlar va estalik sovg‘alar bilan taqdirlandilar.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/buka2.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/buka3.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/buka4.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 00:10:19', '1', 2510, '2019-05-07 00:10:10', '2021-01-06 22:07:12', '/2019- yilgi/05.2019 may/buka1.jpg', 2, NULL, 17, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(87, 'E’tibor va g‘amxо‘rlik...', 'etibor-va-gamxorlik', '<p style=\"text-align: center;\">Tarix va tillar fakulteti talabalari</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika institutining Tarix va tillar fakulteti talabalari, Yoshlar bilan ishlash bо‘yicha dekan о‘rinbosari L.Muhammadiyev boshchiligida Chirchiq shahrida istiqomat qilayotgan urush qatnashchilarining xonadonlariga tashrif buyurishib ularni bayram bilan tabriklashdi. Talabalar tomonidan tayyorlangan qо‘shiqlar, she’rlar va tabriklar barchaga birdek xursandchilik ulashdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/photo_2019-05-06_19-45-41.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/photo_2019-05-06_19-44-45.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 01:39:10', '1', 894, '2019-05-07 01:38:35', '2021-01-06 07:54:28', '/2019- yilgi/05.2019 may/photo_2019-05-06_19-46-22.jpg', 2, NULL, 18, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(88, 'An’anaviy “Tasviriy va amaliy san’at festivali”', 'anaviy-tasviriy-va-amaliy-san-festivali', '<p style=\"text-align: justify;\">An’anaviy “Tasviriy va amaliy san’at festivali”ning ishtirokchilari va mehmonlari 22 aprelga qadar Toshkent shahriga yetib keldilar</p>', '<p style=\"text-align: justify;\">An’anaviy “Tasviriy va amaliy san’at festivali”ning ishtirokchilari va mehmonlari 22 aprelga qadar Toshkent shahriga yetib keldilar va mehmonlarni О‘zbekiston Badiiy akademiyasi Badiiy kо‘rgazmalar direksiyasi direktori - O. Rо‘ziyev, О‘zbekiston Badiiy akademiyasi Badiiy kо‘rgazmalar direksiyasi katta ilmiy xodimi- L.Mirzabayevalar hamda festival tashkilotchilari tomonidan iliq kutib olindi va shu  kuni Kamoliddin Behzod haykali poyiga gul qо‘yish marosimi, An’anaviy “Tasviriy va amaliy san’at festivali”ning hamda О‘zbekistondagi chet el vakolatxonalari madaniyat haftaligining tantanali ochilish marosimi<strong>  </strong>о‘tqazildi.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_4867.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"> Festival doirasida Toshkent shahrining bir qancha kо‘rgazma maydonlarida ochilish marosimlari bо‘lib о‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><strong> </strong>G‘urumsaroy kulolchiligi: Vahob Buvayev kо‘rgazmasi.  Usta bilan ijodiy uchrashuv; Mahorat darsi – Shexovsov Rostislav. Elektron grafika (elektron grafik planshetda grafika);<strong> </strong>Turkmanistonlik rassom Mammed Yarmammedovning kо‘rgazmalarsi ochilishi bо‘lib о‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">    23 aprel kuni  Chingiz Axmarov va uning shogirdi Sodiq Raxmonovning “Mо‘yqalam ila yozilgan nazm” nomli kо‘rgazmasining ochilishi; “Kamoliddin Behzod izdoshlari” Respublika ijodiy tanlovi yakuniy kо‘rgazmasining ochilishi va g‘oliblarni taqdirlash marosimi;  Mahorat darsi - О‘z BA akademigi, professor M.R. Borodina. “Fazo kompozitsiyasi”; О‘z BA Rassomlar ijodiy uyushmasi Halq amaliy san’ati seksiyasining kо‘rgazmasi ochilishi; “Yosh musavvira qizlar” kо‘rgazmasining ochilishi va tanlov g‘oliblarini taqdirlash marosimi  о‘tqazildi.</p>\r\n\r\n<p style=\"text-align: justify;\">    24 aprel kuni  P.Benkov nomidagi Respublika ixtisoslashtirilgan rassomlik maktabi tomonidan tayyorlangan tarixiy liboslar namoyishi; P.Benkov nomidagi Respublika ixtisoslashtirilgan rassomlik maktabi “Kino va televideniye kafedrasi”ning “Sharq kompozitsiyasi”loyihasi; Mahorat darsi - О‘zBA Rassomlar ijodiy uyushmasi Dekorativ amaliy san’ati  seksiyasining N.Kо‘ziyeva, F.Dedenyova  (kulolchilik);  Kamoliddin Behzod nomidagi Milliy rassomlik va dizayn institutining  professor Sobir Rahmetov rahbarligidagi “Ustaxona” loyihasi; Teatr dekoratsiyasi rangtasviri kafedrasi kо‘rgazmasi. Libos dizayni kafedrasi kо‘rgazmasi;  Kamoliddin Behzod nomidagi Milliy rassomlik va dizayn institutining “EGO” eksperimental konferensiyasi; О‘zbekiston Badiiy Akademiyasi akademigi Akmal Ikromjonov va shogirdlarining “Moviyga parvoz - III” nomli kо‘rgazmasi ochilishi bо‘lib о‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">             25 aprel kuni Kamoliddin Behzod nomidagi Milliy rassomlik va dizayn institutining “San’at va badiiy ta’lim: innovatsion jarayonlar va XXI asrga doir samarali о‘zaro ta’sir masalalari” mavzusidagi xalqaro ilmiy-amaliy konferensiyasi; Mahorat darsi - О‘zBA Rassomlar ijodiy  uyushmasi Halq amaliy san’ati  seksiyasi. Yog‘och о‘ymakorligi - Shuxrat Axmednazarov, Toir Fayzullayev, Abdumajid Ubaydullayev, naqqoshlik - G‘ayrat Shokirov, Abdujamil Tо‘laganov; Kamoliddin Behzod nomidagi Milliy rassomlik va dizayn instituti tomonidan tayyorlangan zamonaviy va an’anaviy liboslar namoyishi bо‘lib о‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_6209.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">           26 aprel kuni Respublika ixtisoslashtirilgan san’at maktab-internati tomonidan tayyorlangan konsert namoyishi; Respublika ixtisoslashtirilgan dizayn maktabi tomonidan tayyorlangan zamonaviy va an’anaviy liboslar namoyishi; - Mahorat darsi - О‘zBA Rassomlar ijodiy uyushmasi Halq amaliy san’ati  seksiyasi. Ganch о‘ymakorligi – Umarjon Toxirov, Bahodir Sultonov, Faxriddin Xamzayev, kulolchilik – Abduvosid Mamajonov tomonidan maxorot  darslari bо‘lib о‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">            27 aprel kuni An’anaviy “Tasviriy va amaliy san’at festivali”ning yopilish hamda festival sovrindorlari va qatnashchilarni taqdirlash (medal, diplom va sertifikatlar) marosimlari bо‘lib о‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Festival tadbirlari va jarayon ommaviy axborot vositalari orqali keng yoritillib borildi.</p>\r\n\r\n<p style=\"text-align: justify;\">“Tasviriy va amaliy san’at festivali” bir necha yildan beri halqaro miqyosda nishonlanib – Eron, Quvayt, Ozarbayjon, Qozog‘iston,  Latviya va boshqa davlatlarning О‘zbekistondagi diplomatik vakolatxonalari о‘z madaniyatlarini yuqori professional saviyada bajarilgan san’at  asarlarini  tomoshabinlarga namoish qilib kelmoqdalar.</p>\r\n\r\n<p style=\"text-align: justify;\">Bundan tashqari festival davomida Badiiy Kо‘rgazmalar direksiyasining Markaziy kо‘rgazmalar zali hamda Kamoliddin Behzod nomidagi Sharq miniatyura muzeyi bog‘ hududi atrofida quyidagi о‘quv muassasalarining о‘qituvchilari – mahorat-darslari va talabalarining – ijodiy  ishlari  taqdimoti bо‘lib о‘tdi:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">Kamoliddin Behzod nomidagi Milliy rassomlik va dizayn instituti</li>\r\n	<li style=\"text-align: justify;\">Toshkent Arxitektura qurilish instituti</li>\r\n	<li style=\"text-align: justify;\">-  Nizomiy nomidagi Toshkent Davlat Pedagogika universiteti</li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><em>-   </em><em>I. Karimov nomidagi  Toshkent Davlat texnika universiteti. </em></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">-  Toshkent viloyati Chirchiq Davlat pedagogika instituti</li>\r\n	<li style=\"text-align: justify;\">-  P.Benkov nomidagi Respublika ixtisoslashtirilgan rassomlik maktabi</li>\r\n	<li style=\"text-align: justify;\">-  Respublika ixtisoslashtirilgan dizayn maktabi</li>\r\n	<li style=\"text-align: justify;\">-  Qо‘qon ixtisoslashtirilgan san’at maktabi</li>\r\n	<li style=\"text-align: justify;\">-  Yunusobod dizayn kasb-hunar kolleji</li>\r\n	<li style=\"text-align: justify;\">-  Toshkent Arxitektura-qurilish kasb-hunar kolleji</li>\r\n	<li style=\"text-align: justify;\">-  Respublika ixtisoslashtirilgan san’at maktab-internati</li>\r\n	<li style=\"text-align: justify;\">- Qoraqalpog‘iston va viloyat ixtisoslashtirilgan san’at maktablari va maktab internatlari</li>\r\n	<li style=\"text-align: justify;\">-  Shuningdek, Toshkent shahar va viloyatlardagi Oliy va о‘rta maxsus, - umumta’lim о‘quv muassasalari ishtirok etishdi.</li>\r\n</ul>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_5603.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">Shu jumladan festivalda Toshkent viloyati  Chirchiq Davlat pedagogika instituti “Tasviriy san’at va mehnat  ta’limi” kafedrasi professor‑о‘qituvchilari va yо‘nalish talabalari faol ishtirok etib, festival tashkilotchi, ishtirokchi hamda tomoshabinlarda iliq tasurotlarni qoldirdilar. Toshkent viloyati  Chirchiq Davlat pedagogika instituti professor‑о‘qituvchilari va talabalari mahorat-darslari va ijodiy  ishlari  taqdimoti festival davomida bо‘lib о‘tdi. Institutning ochilganiga endigina ikki yil bо‘lganiga qaramasdan Respublikada munosib о‘ringa  ega ekanligini professor‑о‘qituvchilari va talabalarining festivalda olgan diplom, sertifikat, tashakkurnomalari anglatib turibdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Toshkent viloyati  Chirchiq Davlat pedagogika instituti “Tasviriy san’at va mehnat  ta’limi” kafedrasi professor‑о‘qituvchilari:</p>\r\n\r\n<p style=\"text-align: justify;\">Sultanov Xaytboy Eraliyevich kafedra v.b dotsenti, Muratov Husan Xolmuratovich kafedra katta о‘qituvchisi, Sobirov Sarvar Tursunmuratovich kafedra о‘qituvchisi, Ismatov Ulfat Shuhratovich kafedra о‘qituvchisi,  Achilov Nurbek  Norboy о‘g‘li kafedra о‘qituvchisi, Bekqulov Qudrat Shaydullayevich kafedra о‘qituvchilari О‘zbekiston Badiiy Akademiyasi tomonidan diplom bilan taqdirlandi.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_6285.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">           “Tasviriy san’at va muhandislik grafikasi” yо‘nalishi talabasi Tolipova Marjona О‘zbekiston Badiiy Akademiyasi tomonidan <strong>Kumush</strong> medal va diplom bilan taqdirlandi. Qolgan talabalarimiz ham  О‘zbekiston Badiiy Akademiyasi tomonidan diplom bilan taqdirlandilar.</p>\r\n\r\n<p style=\"text-align: justify;\">Jumladan:</p>\r\n\r\n<p style=\"text-align: justify;\">        Todjiddinov Muhriddin, Baxtiyorova Zarnigor, Eshpо‘latov Doniyor, Isomedova Gavhar, Izzatullayeva Durdona, Xabibullin Maqsud, Jabborrov Davlatbek, Jumayev Jumanazar, Narimonov Elshod, Rixsiboyeva Munisa, Yо‘lchiyeva О‘g‘iloy, Berdiyev Doston, Mirjahparova Maxliyo, Voxidova Nigora, Mamatqulova Mamura, Yun Polina, Boymurodov Nurali, Tursunqulova Dilnoza, Mustafaqulova Farida, Yunusova Kamola Tulaganova Xilola.</p>\r\n\r\n<p style=\"text-align: justify;\">         Quyidagi talabalari bо‘lsa О‘zbekiston Badiiy Akademiyasi tomonidan faol ishtiroki uchun sertifikatlar bilan taqdirlandilar.</p>\r\n\r\n<p style=\"text-align: justify;\">Jumladan:</p>\r\n\r\n<p style=\"text-align: justify;\">Rustamova Ra’no, Raximjonova Nigora, Orzumatova Nigina, Tursunboyeva Muxlisa, Abdunazarova Xosiyat, Jangaboyev Ulug‘bek, Ikramova Mamura, Jakyayeva Dinara, Narimonova Shodiya,  Saparova Rashida, Tо‘xtaboyeva Laylo, Agzamov Doniyor, Mirsoatova Laylo, Nodirova Zaxro, Truxanova Svetlana, Chubarova Galina.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"> “Tasviriy va amaliy san’at festivali” doirasida О‘zbekiston Badiiy Akademiyasi Qoraqalpog‘iston va viloyat tizim tashkilot va muassasalarida bir vaqtning о‘zida kо‘rgazmalar, mahorat darslari, tanlovlar va ijodiy uchrashuvlar bо‘lib о‘tdi. Shu munosabat bilan Sirdaryo viloyatida ham festival bо‘lib о‘tdi. Sirdaryodagi festivalda Toshkent viloyati  Chirchiq Davlat pedagogika instituti “Tasviriy san’at va mehnat  ta’limi” kafedrasi v.b dotsenti Sultanov Xaytboy Eraliyevich va talabalarimiz munosib ishtirok etishdi. Ustoz va talabalarimiz Sirdaryolik san’at ixlosmantlari va tomoshabinlarga maxorat-darslari kо‘rsatish jarayonida  ularda iliq tasurotlar qoldirishdi. Sirdaryolik  ustozlar va talabalar bilan tajriba  almashishdi.</li>\r\n	<li style=\"text-align: justify;\">Ustozlarimiz va talabalarimizning Sirdaryodagi festivaldan bir dunyo  tasurot va yutuqlar bilan qaytishdi.</li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\">Toshkent viloyati  Chirchiq Davlat pedagogika instituti “Tasviriy san’at va mehnat  ta’limi” kafedrasi professor‑о‘qituvchilari:</p>\r\n\r\n<p style=\"text-align: justify;\">Kafedra v.b dotsenti: Sultanov Xaytboy Eraliyevich, kafedra katta о‘qituvchisi: Muratov Husan Xolmuratovich Sirdaryo viloyati Badiiy  Aademiyasi tomonidan DIPLOM,  Sirdaryo viloyati xokimi tomonidan TASHAKKURNOMA bilan taqdirlandilar.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">Ushbu talabalarimizning xar biri Sirdaryo viloyati Badiiy  Aademiyasi tomonidan DIPLOM,  Sirdaryo viloyati xokimi tomonidan TASHAKKURNOMA bilan taqdirlandilar.</li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\">              Jumladan:</p>\r\n\r\n<p style=\"text-align: justify;\">        Jumayev Jumanazar, Rixsiboyeva Munisa, Berdiyev Doston, Boymurodov Nurali,  Mustafaqulova Farida, Yunusova Kamola.</p>\r\n\r\n<p style=\"text-align: justify;\">Sirdaryolik  rassom “Dо‘stlik”ordeni sohibi Anorboy Boybekov ta’biri  bilan aytganda “Uyquni kam qilib, mehnatni kо‘p qilsangiz maqsadga erishasiz”.</p>\r\n\r\n<p style=\"text-align: justify;\">Talabalarimiz bunday yutuqlar va ijodiy ishlar davomiy bо‘lishi yо‘lida tinimsiz mehnat, izlanishda bо‘lish kerakligini anglab yetishdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_6360.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-05-07 06:26:25', '1', 1672, '2019-05-07 06:26:25', '2021-01-06 12:50:30', '/2019- yilgi/05.2019 may/IMG_6357.jpg', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(89, '“XOTIRA - AZIZ, QADR - MUQADDAS”', 'xotira-aziz-qadr-muqaddas', '<p style=\"text-align: center;\">96 yoshli Juchkov Vasiliy Mixailovich</p>', '<p>Toshkent viloyati Chirchiq davlat pedagogika instituti Tarix va tillar fakulteti “О‘zbek tili va adabiyoti” yо‘nalishi 18-1- guruh talabalari Chirchiq shahri Sportchilar kо‘chasi 30-uy 6-xonadonda istiqomat qilayotgan  Ulug‘ Vatan urushi qatnashchisi, 1923 yilda tug‘ilgan, 96 yoshli Juchkov Vasiliy Mixailovich holidan xabar olishdi. Uchrashuvda talabalar Ulug‘ Vatan urushi yillari voqealari haqida yorqinroq tasavvurga ega bо‘lishdi.</p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/uploaded-files/IMG_20190507_134347_851.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/IMG_20190507_134400_595.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 06:47:46', '1', 1005, '2019-05-07 06:30:28', '2021-01-07 00:11:33', '/2019- yilgi/05.2019 may/IMG_20190507_134420_260.jpg', 2, NULL, 18, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(90, 'Talablar urush qatnashchilari bilan uchrashuv o\'tkazishdi.', 'talablar-urushda-qatnashchilari-bilan-uchrashuv-otkazishdi', '<p style=\"text-align: justify;\">Aniq va tabiiy fanlar fakukteti Tasviriy san\'at va muhandislik grafikasi yonalishi 17/1, 18/2 guruh talabalari hamda professor o’qituvchilari</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Aniq va tabiiy fanlar fakukteti Tasviriy san\'at va muhandislik grafikasi yonalishi 17/1, 18/2 guruh talabalari hamda professor o’qituvchilari Chirchiq shaxri Navoiy kochasida yashovchi \"Ulug\' vatan urushi qatnashchisi\" 92 yoshli  Grigoriy Lazerovichni 9-may Hotira va qadirlash kuni bayrami munosabati bilan tabriklagani borishdi. Golber Grigoriy Lazerov 1928-yilda tavllud topgan hamda Ulug\' vatan urushida qatnashgan. Tula shaxrida bo\'lgan urush tavsilotlari urushning oqibatlarini begunoh odamlarning ko\'z o’ngida jon bergani haqida gapirib berdi. Hamda tinchligimiz oliy ne\'mat tinchlikning qadriga yetishimizni tilab qoldi.Grigoriy Lazerovich Xalqlar dostiligi ordeni, G\'alaba ordenlari bilan taqdirlangan.Talabalar o’zlariga kerakli ma\'lumotlarga ega bo\'lishdi. Grigoriy Lazerovich katadan kata rahmatlarni ishor etdi.</p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7951.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7969.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/IMG_7947.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>', '2019-05-07 06:32:02', '1', 988, '2019-05-07 06:32:02', '2021-01-01 15:55:50', '/2019- yilgi/05.2019 may/IMG_7983.jpg', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(91, 'Innovatsion klaster tizimi Oqqо‘rg‘on tumanida', 'innovatsion-klaster-tizimi-oqqorg-tumanida', '<p style=\"text-align: center;\">Innovatsion klaster</p>', '<p style=\"text-align: justify;\">Tarix va tillar fakultetit “Rus tili va adabiyoti kafedrasi” о‘qituvchilari Oqqо‘rg‘on tumanida kо‘chma seminar mashg‘ulotini о‘tkazdilar. Seminarda Yangiyо‘l shahri, Yangiyо‘l, Chinoz tumanlarining rus tili va adabiyoti fani о‘qituvchilari ishtirok etdilar. Innovatsion klaster bо‘yicha rus tili va adabiyoti kafedra о‘qituvchilari tomonidan “Master-klass” о‘tkazildi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/photo_2019-05-07_17-07-01.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 07:09:32', '1', 1309, '2019-05-07 07:08:59', '2021-01-07 03:51:13', '/2019- yilgi/05.2019 may/photo_2019-05-07_17-06-56.jpg', 2, NULL, 9, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(92, 'МAKTAB LABORATORIYASI', 'maktab-laboratoriyasi', '<p style=\"text-align: center;\">Chirchiq shahar 15-sonli umumiy o’rta ta’lim maktabida</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Tarix va tillar fakultetining: o’zbek tili va adabiyoti, rus tili va adabiyoti va xorijiy tillar &nbsp;yo’nalishlari bo’yicha Chirchiq shahar 15-sonli umumiy o’rta ta’lim maktabi qoshida «Мaktab laboratoriyasi» tashkil etildi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/%D0%9C%D0%B0%D0%BA%D1%82%D0%B0%D0%B12.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 07:15:02', '1', 981, '2019-05-07 07:15:02', '2021-01-07 03:51:13', '/2019- yilgi/05.2019 may/Lochin.jpg', 1, NULL, 9, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(93, 'O’QITISHDA KLASTER YONDASHUV', 'oqitishda-klaster-yondashuv', '<p style=\"text-align: center;\">Amaliy sayyor seminar</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Tarix va tillar fakulteti rus tili va adabiyoti kafedrasi professor-o’qituvchilari va umumiy o’rta ta’lim maktablari oqituvchilari  bilan hamkorlikda 2019 yil 23 aprel kuni «Umumiy o’rta ta’lim maktablarida rus tili va adabiyoti fanini o’qitishda klaster yondashuv” mavzusida ilmiy- amaliy sayyor seminar o’tkazildi. Seminar Chirchiq shahar 15-sonli umumiy o’rta ta’lim maktabida bo’lib o’tdi. Seminarda Chirchiq shahri, Bo’stonliq, Parkent, Qibray va Yuqori Chirchiq tumanlari rus tili va adabiyoti fani o’qituvchilari ishtirok etdilar.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/%D0%9A%D0%BB%D0%B0%D1%81%D1%82%D0%B5%D1%802.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 07:16:46', '1', 1165, '2019-05-07 07:16:21', '2021-01-07 03:51:13', '/2019- yilgi/05.2019 may/Кластер1.jpg', 2, NULL, 11, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(94, 'Ajdodlarimiz doimo e’zozda', 'ajdodlarimiz-doimo-ezozda', '<p style=\"text-align: center;\">Yusufbek Veliyev xonadonida</p>', '<p style=\"text-align: justify;\">7-may kuni Tarix va ijtimoiy fanlar kafedrasi yetakchiligida Milliy g‘oya, ma’naviyat asoslari va huquq ta’limi yо‘nalishi 2-bosqich talabalari Ikkinchi jahon urushi faxriysi, Chirchiq shaxar A.Navoiy kо‘chasi 26-sonli mahalla 254/11-xonada istiqomat qiluvchi 1924 yilda tug‘ilgan Yusufbek Veliyev xonadonida bо‘ldilar. О‘zaro suhbat jarayonida talabalar xalqimizning urush yillaridagi tengsiz jasorati haqida ibratli voqealarni, Tinchlik nechog‘lik ulug‘ ne’matligini hayotiy tajribalar misolida tingladilar.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/yusuf1.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-07 07:59:10', '1', 1109, '2019-05-07 07:59:10', '2021-01-07 03:51:13', '/2019- yilgi/05.2019 may/yusuf2.png', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(95, 'KEKSALARIMIZ DOYIM E`ZOZDALAR', 'keksalarimiz-doyim-ezozdalar', '<p style=\"text-align: justify;\">Pedagogika fakulteti Maktabgacha va boshlang`ich ta`lim sporti kafedrasi  Maktabgacha taʼlim yoʻnalishi 18/2 guruh talabari</p>', '<p align=\"center\" style=\"text-align: justify;\">Pedagogika fakulteti Maktabgacha va boshlang`ich ta`lim sporti kafedrasi  Maktabgacha taʼlim yoʻnalishi 18/2 guruh talabari va guruh rahbari 9- may Xotira va qadirlash kuni munosabati bilan Boʻstonliq tumani Xumson qishlogʻi \"Oq machit \" koʻchasi 28-xonadonda yashovchi 95 yoshli ulug’ vatan urushida mardonavor jang olib borgan Mirsoliq ota Ergashov xonadoniga tashrif buyurdilar.Otaxonga yoshlar nomidan milliy chopon, doʻppi va belbogʻ taqdim etdilar.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/Pedagogika%20fakulteti%20Maktabgacha%20va%20boshlang4.png\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/Pedagogika%20fakulteti%20Maktabgacha%20va%20boshlang.png\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/Pedagogika%20fakulteti%20Maktabgacha%20va%20boshlang3.png\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>', '2019-05-08 05:46:49', '1', 1182, '2019-05-08 05:46:49', '2021-01-07 03:51:13', '/2019- yilgi/05.2019 may/Pedagogika fakulteti Maktabgacha va boshlang2.png', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(96, 'ARUZ NAZARIYASI ASOSLARI', 'aruz-nazariyasi-asoslari', '<p>Toshkent viloyati Chirchiq davlat pedagogika instituti  Tarix va tillar fakulteti О‘zbek tili va adabiyoti yо‘nalishi talabalari bilan 2019 yil 8 may kuni 2 va 3 juftlikda</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti  Tarix va tillar fakulteti О‘zbek tili va adabiyoti yо‘nalishi talabalari bilan 2019 yil 8 may kuni 2 va 3 juftlikda Alisher Navoiy nomidagi О‘zbek tili va adabiyot universiteti Mumtoz adabiyot kafedrasi dotsenti Dilnavoz Yusupova “Aruz nazariyasi asoslari” mavzuida ochiq muloqot darsi bо‘lib о‘tdi. Mazkur darsda Aruz vaznining kelib chiqishi, mumtoz va hozirgi о‘zbek adabiyoti lirikasida ahamiyati haqida sо‘z yuritildi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/20190508_104519.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/20190508_104855.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>', '2019-05-08 06:17:59', '1', 3190, '2019-05-08 06:02:36', '2021-01-07 03:51:13', '/2019- yilgi/05.2019 may/20190508_104845.jpg', 1, NULL, 5, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(97, '“Sharaf lentasi  - tinchlik shukronasi”', 'sharaf-lentasi-tinchlik-shukronasi', '<p style=\"text-align: justify;\">2019 yil 7 may kuni Toshkent viloyati Chirchik pedagogika instituti talabalari 9 may - “Xotira va Qadrlash kuni” munosabati bilan Chirchiq Oliy tank qо‘mondon</p>', '<p style=\"text-align: justify;\">2019 yil 7 may kuni Toshkent viloyati Chirchik pedagogika instituti talabalari 9 may - “Xotira va Qadrlash kuni” munosabati bilan Chirchiq Oliy tank qо‘mondon muhandislik bilim yurti kursantlariga “Sharaf lentalari” ulashishdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/sharaf-lentasi-tinchlik-shukronasi4.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/sharaf-lentasi-tinchlik-shukronasi2.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/sharaf-lentasi-tinchlik-shukronasi5.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>', '2019-05-08 06:23:37', '1', 1842, '2019-05-08 06:23:37', '2021-01-07 03:49:47', '/2019- yilgi/05.2019 may/sharaf-lentasi-tinchlik-shukronasi.jpg', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(98, 'Keksalarimiz e’zozda', 'keksalarimiz-ezozda2', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Aniq va tabiiy fanlar fakulteti talabalari 2019 yil 7 may kuni Qibray tumanida istiqomat qilib</p>', '<p align=\"center\" style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Aniq va tabiiy fanlar fakulteti talabalari 2019 yil 7 may kuni Qibray tumanida istiqomat qilib kelayotgan bir qator urush qatnashchilari xolidan habar olib, ularni bayram bilan tabriklab,esdalik sovg‘alari taqdim etdilar. Jumaladan Qibray shaharchasi Dadaboyev kо‘chasi 14 uyda istiqomat qilib kelayotgan urush qatnashchisi 1925 yilda tug‘ilgan Atakayev Mо‘min ota, Qibray tumani Oq-qovoq qishlog‘i Vir botanika kо‘chasi 1 uy 23 xonadonda istiqomat qiluvchi 1924 yilda tug‘ilgan Maxanov Georgiy ota, Qibray tumanida yashovchi  ikkinchi jaxon urushi qatnashchisi Saitnasirov Burdabay va Mehnat faxriylari Alekseykov Vladimir, Aburazzokov Abdug‘ani otalarni bayram bilan tabrikladi, urush va mehnat faxriylari talaba-yoshlarga Vatan himoyasi va ozodligi yо‘lida mardona kurashganligi, ming- minglab mard ota-bobolarimiz, onalarimizning matonati va jasorati insoniyatni halokatdan asrab qolgani, jang maydonlarida jon berib, qon kechib kо‘rsatgan qaxramonliklar kо‘rsatgani haqida hikoya qilishdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Talaba-yoshlar Toshkent shahrida joylashgan Motamsaro ona Xotira maydoniga ham tashrif buyurishib Motamsaro ona poyiga gullar qо‘yishdi.  Bayram munosabati bilan talaba-yoshlar Qibray tumanida joylashgan  Dо‘rmon madaniyat saroyida 2-jahon urushi qatnashchilarini bayram bilan tabriklashdi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/photo_2019-05-07_17-28-24.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/photo_2019-05-07_17-47-51.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/2019-%20yilgi/05.2019%20may/photo_2019-05-07_17-26-30.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-05-08 06:28:35', '1', 1386, '2019-05-08 06:28:35', '2021-01-07 03:49:47', '/2019- yilgi/05.2019 may/photo_2019-05-08_15-13-43.jpg', 1, NULL, 18, 0, '', 1, NULL, NULL, NULL, NULL, NULL),
(99, 'ENDI BARCHA YО‘LLAR RIMGA ELTAR...', 'endi-barcha-yollar-rimga-eltar', '<p style=\"text-align: center;\">“SHYMKENT MUSIC GAMES”</p>', '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Musiqa ta’limi yо‘nalishi jamoasi – о‘qituvchilar Shohida Mahkamova, Malohat Muhitdinova hamda 1-kurs talabalari Sobirjon Toshmetov, Shukurbek Halilov, Sherzod Toshmatovlar 2019 yil 9 may kuni Qozog‘iston Respublikasining Chimkent shahrida о‘tkazilgan “SHYMKENT MUSIC GAMES” respublika kо‘p janrli musiqa tanlovining Cholg‘u ijrochiligi bо‘yicha о‘tkazilgan kо‘rik tanlovda 1-о‘rinni qо‘lga kiritib 1-darajali diplom bilan taqdirlanishdi. Ushbu tanlov ushbu jamoa uchun 2019 yil 29 iyun kuni Qirg‘iziston Respublikasining Issiqkо‘l shahrida о‘tkaziladigan xalqaro tanlovga hamda 2020 yil 7 mart kuni Italiyaning Rim shahrida о‘tkaziladigan xalqaro musiqa tanlovida ishtirok etish uchun yо‘llanma berdi. Institutimizning Ulug‘ masadini rо‘yobga chiqarish yо‘lida qо‘ygan dadil qadami uchun 1-kurs Musiqa ta’limi yо‘nalishi talabalarini samimiy tabriklaymiz. Ulug‘ maqsad yо‘lida boshingiz omon, tafakkuringiz tiyran va istiqbolingiz porloq bо‘lsin, azizlar!</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/%D0%A8%D0%B8%D0%BC%D0%BA%D0%B5%D0%BD%D1%822.png\" style=\"width: 50%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/%D0%A8%D0%B8%D0%BC%D0%BA%D0%B5%D0%BD%D1%823.png\" style=\"width: 50%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-10 05:37:07', '1', 1317, '2019-05-10 05:37:07', '2021-01-07 03:49:47', '/2019- yilgi/05.2019 may/Шимкент1.jpg', 2, NULL, 18, 0, '', 0, NULL, NULL, NULL, NULL, NULL),
(100, 'Maktab laboratoriyasi', 'maktab-laboratoriyasi-1', '<p style=\"text-align: center;\">“Maktab laboratoriyasi”ning ochilishi</p>', '<p style=\"text-align: justify;\">Tarix va tilla fakulteti Rus tila va adabiyoti kafedrasi professor-о‘qituvchilari tomonidan Chirchiq shahrida joylashgan 15-umumta’lim maktabida Institut va maktab hamkorligida faoliyat boshlagan “Maktab laboratoriyasi”ning ochilishi munosabati bilan ma’naviy-ma’rifiy tadbir tashkil etildi.</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/%D0%9C%D0%B0%D0%BA2.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"><img src=\"http://cspi.uz/storage/app/media/uploaded-files/%D0%9C%D0%B0%D0%BA3.jpg\" style=\"width: 75%;\" class=\"fr-fic fr-dib\" data-result=\"success\"></p>', '2019-05-11 01:28:57', '1', 1577, '2019-05-11 01:28:50', '2021-01-06 18:23:05', '/2019- yilgi/05.2019 may/Мак1.jpg', 2, NULL, 18, 0, '', 0, NULL, NULL, NULL, NULL, NULL);
