(165, 'PEDAGOGIK-PSIXOLOGIK TRENING MASHG‘ULOTI..', 'malumotnoma', '<p>Pedagogika fakulteti va Chirchiq shaxar xalq ta’limi boshqarmasiga &nbsp;qarashli 2-sonli...</p>', '<p style=\"text-align: justify;\" align=\"center\">Pedagogika fakulteti va Chirchiq shaxar xalq ta’limi boshqarmasiga  qarashli 2-sonli umumiy o‘rta ta’lim maktabida “O‘quvchilar o‘rtasida noxush xolatlarni oldini olish” mavzusida  pedagogik, psixologik trening mashg‘uloti bo‘lib o‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\"> “Pedagogika va menejmen” kafedrasi o‘qituvchilari Ismoilova Dilafruz Muxiddinovna va Raxmonov Azamat Rizvonovichlar pedagogik, psixologik trening mashg‘ulotini olib bordilar.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/malumotnioma-2.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-10-14 23:46:58', '1', 2195, '2019-10-14 23:46:58', '2021-01-06 22:21:41', '/2019- yilgi/10.2019/malumotnioma-1.jpg', 1, NULL, 18, 0, '', 1, NULL, '', '', '', ''),
(166, 'Ilmiy tadqiqot ishlarini olib borishning tashkiliy metodik asoslari', 'ilmiy-tadqiqot-ishlarini-olib-borishning-tashkiliy-metodik-asoslari', '<p>Maktabgacha va boshlang‘ich ta’lim fakulteti va Pedagogika fakulteti hamkorligida</p>', '<p align=\"center\" style=\"text-align: justify;\">Maktabgacha va boshlang‘ich ta’lim fakulteti va Pedagogika fakulteti hamkorligida ilmiy tadqiqot ishini boshlash istagida bo‘lgan yosh mutaxasislar  uchun seminar tashkil etildi. Seminarda kafedra professor o‘qituvchilari tomonidan ilmiy maqola va ilmiy tadqiqot ishlarning tashkiliy metodik asoslari to‘g‘risida ma’lumotlar berildi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/seminarlar-2.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib oc-img-bordered\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/seminarlar-4.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib oc-img-bordered\"></p>\r\n\r\n<p><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/seminarlar-3.jpg\" style=\"width: 100%;\" class=\"fr-fic fr-dib oc-img-bordered\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p>\r\n	<br>\r\n</p>', '2019-10-14 23:51:32', '1', 2756, '2019-10-14 23:51:32', '2021-01-07 00:08:39', '/2019- yilgi/10.2019/seminarlar-1.jpg', 1, NULL, 11, 0, '', 1, NULL, '', '', '', ''),
(167, 'ONA TILIM, SEN BORSAN, SHAKSIZ BULBUL KUYIN SHE’RGA SOLAMAN...', 'ona-tilim-sen-borsan-shaksiz-bulbul-kuyin-sherga-solaman', '<p>Toshkent viloyati Chirchiq davlat pedagogika institutida 2019 yil 16 oktabr kuni</p>', '<p style=\"text-align: justify;\" align=\"center\">Toshkent viloyati Chirchiq davlat pedagogika institutida 2019 yil 16 oktabr kuni O‘zbekiston Respublikasining “Davlat tili to‘g‘risida”gi Qonuni qabul qilinganligining 30 yilligi munosabati bilan O‘zbekiston Qahramoni, O‘zbekiston Xalq shoiri Abdula Oripov ijodiga bag‘ishlangan “Ona tilim, sen borsan, shaksiz Bulbul kuyin she’rga solaman...” deb nomlangan adabiy-ma’rifiy tadbir o‘tkazildi.</p>\r\n\r\n<p style=\"text-align: justify;\">Tadbir Tarix va tillar fakulteti O‘zbek tili va adabiyoti kafedrasi tomonidan ko‘tarinki ruhda tashkil etildi.</p>\r\n\r\n<p style=\"text-align: justify;\">Kechada O‘zbekiston Yozuvchilar Uyushmasi raisi, O‘zbekiston xalq shoiri Sirojiddin Sayyid, O‘zbekiston Jurnalistlar ijodiy uyushmasi raisi, O‘zbekistonda xizmat ko‘rsatgan madaniyat xodimi Ca’dulla Hakim, O‘zbekiston Milliy Axborot agentligi Bosh direktori Abdusaid Ko‘chimov, O‘zbekiston Yozuvchilar uyushmasi Toshkent viloyati bo‘limi raisi o‘rinbosari Botirali Ergashev, “Favvora film” studiyasi badiiy rahbari Keldiyor Xo‘jayorov hamda shoirning turmush o‘rtog‘i Hanifa Mustafaevalar ishtirok etishdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Alisher Navoiy nomidagi Toshkent davlat o‘zbek tili va adabiyoti universiteti professori, filologiya fanlari doktori Nurboy Jabborovning “Abdulla Oripovning milliy she’riyatimiz taraqqiyotida tutgan o‘rni” mavzusidagi ilmiy ma’ruzasi shoir fenomenini anglashda katta ahamiyat kasb etdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Tadbir ishtirokchilari ona tilimiz jozibasini Abdulla Oripov ijodi orqali yana bir bor his etdilar.</p>\r\n\r\n<p style=\"text-align: justify;\">Institutning Musiqa ta’limi yo‘nalishi talabalari ijrosidagi Abdulla Oripov qalamiga mansub “O‘zbekiston – vatanim manim!” qo‘shig‘i barchaga unutilmas lahzalarni in’om etdi.</p>\r\n\r\n<p style=\"text-align: justify;\">                Tadbir so‘nggida shoirning rafiqasi Hanifa aya Mustafaeva Abdulla Oripovning ijod laboratoriyasi, ona tili – o‘zbek tilini sevish haqidagi xotiralari bilan o‘rtoqlashdi. Garchi pedagog bo‘lishiga qaramasdan, Hanifa ayaning shoir vafotiga bag‘ishlab aytgan she’ri barchani to‘lqinlantirdi. O‘zbek tili yashar ekan, millat yashaydi, millat bor ekan,  Abdulla Oripovdek  fidoyi farzandlari uning qalbida mangu yashaydi.</p>\r\n\r\n<p style=\"text-align: center;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3542-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3621-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3517-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3620-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3562-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3736-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3735-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3737-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\" align=\"center\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3739-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_3667-min.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">Institut axborot xizmati</p>', '2019-10-17 01:35:30', '1', 4974, '2019-10-17 01:35:30', '2021-01-06 20:07:15', '/2019- yilgi/10.2019/IMG_3574-min.jpg', 1, NULL, 20, 0, '', 1, NULL, '', '', '', ''),
(168, 'TIL – MILLAT KO‘ZGUSI', 'til-millat-kozgusi', '<p>Tarix va tillar fakultetining faollar zalida O‘zbek tili va adabiyoti kafedrasi,</p>', '<p style=\"text-align: justify;\">Tarix va tillar fakultetining faollar zalida O‘zbek tili va adabiyoti kafedrasi, Qozoq va tojik ta’lim &nbsp;yo‘nalishlarning O‘zbekiston Respublikasining “Davlat tili haqida”gi Qonuni qabul qilinganligining 30 yilligi munosabati bilan “Til – millat ko‘zgusi” deb nomlangan adabiy-ma’rifiy tadbir bo‘lib o‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Tadbirini qozoq va tojik tili ta’lim yo‘nalishi professor o‘qituvchilari, talabalari tomonidan ko‘tarinki ruhda tashkil etildi.</p>\r\n\r\n<p style=\"text-align: justify;\">Adabiy-ma’rifiy kechada Tarix va tillar fakulteti dekani, dotsent Ravshan Ikramov kirish so‘z bilan ochib berdi.</p>\r\n\r\n<p style=\"text-align: justify;\">O‘zbek tili va adabiyoti kafedrasi mudiri, dotsent Abdushukur Shofqarov &nbsp;“Til – millatning ma’naviy boyligi” mavzusidagi &nbsp;ma’ruzasi bilan ishtirok etdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Qozoq va tojik tili ta’lim yo‘nalishi talabalari &nbsp; o‘zbek, qozoq va tojik tillarida she’rlar, allomalarning til haqidagi ibratli so‘zlari, “Davlat tili haqida”gi Qonun moddalari, maqol-matallardan namunalar keltirishdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Tadbir so‘ngida qozoq va tojik tillarida talaba qizlar o‘zlarining milliy raqslarini namoyish etishdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Tadbir ishtirokchilarda katta taassurot qoldirdi.</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;<strong><br></strong>&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><strong>&nbsp;</strong></p>', '2019-10-19 08:20:56', '1', 5294, '2019-10-19 08:20:56', '2021-01-02 10:34:50', '/2019- yilgi/10.2019/photo_2019-10-19_18-20-23.jpg', 1, NULL, 6, 0, '', 1, NULL, '', '', '', ''),
(169, 'TIL – MILLAT KO‘ZGUSI', 'til-millat-kozgusi-institut', '<p>&nbsp; &nbsp;21 oktabr – O‘zbekiston Respublikasining “Davlat tili haqida”gi</p>', '<p align=\"center\" style=\"text-align: justify;\">&nbsp; &nbsp; 21 oktabr – O‘zbekiston Respublikasining “Davlat tili haqida”gi Qonuni qabul qilinganining 30 yilligi munosabati bilan Toshkent viloyati Chirchiq davlat pedagogika institutida<strong>&nbsp;</strong>21 oktabr kuni<strong>&nbsp;</strong>“Til – millat ko‘zgusi” deb nomlangan adabiy-ma’rifiy tadbir tashkil etildi.</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50.0000%;\">\r\n				<div style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_2997.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></div>\r\n			</td>\r\n			<td style=\"width: 50.0000%;\">\r\n\r\n				<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_2797.jpg\" class=\"fr-fic fr-dii\" style=\"width: 95%;\"></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</em></strong></p>\r\n\r\n<p style=\"text-align: justify;\"><strong><em>&nbsp;</em></strong>Tadbirda tilshunos olimlar, huquq-tartibot sohasi vakillari va talaba yoshlar ishtirok etdilar. Tadbirda so‘zga chiqqanlar til – millatning ma’naviy boyligi ekanligi, til nafaqat muomala vositasi, balki xalqning madaniyati, urf-odati, uning turmush tarzi, tarixi hisoblanishi, shu boisdan Ona tilimizning xalqaro miqyosdagi obro‘-e’tiborini yuksaltirishda, uni milliy va umumbashariy tushunchalar asosida taraqqiy etgan tillar safiga qo‘shishda, tilimizga chuqur hurmat bilan yondashishda barcha birdek mas’ul ekanligi to‘g‘risida gapirdilar.</p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_2786.jpg\" class=\"fr-fic fr-dii\" style=\"width: 90%;\"></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tadbir yakunida Tarix va tillar fakulteti o‘zbek tili va adabiyoti kafedrasi tashabbusi bilan o‘tkazilgan o‘zbek tili haftaligi doirasidagi tanlovlarning g‘oliblari institut tashakkurnomasi va diplomlari hamda qimmatbaho sovg‘alari bilan taqdirlandi.</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50.0000%;\">\r\n				<div style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_2958.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></div>\r\n			</td>\r\n			<td style=\"width: 50.0000%;\">\r\n				<div style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/IMG_2830.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"right\"><strong>Institut axborot xizmati</strong></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p>\r\n	<br>\r\n</p>', '2019-10-22 02:37:59', '1', 2981, '2019-10-22 02:37:59', '2021-01-07 03:53:46', '/2019- yilgi/10.2019/IMG_2995.jpg', 1, NULL, 18, 0, '', 1, NULL, '', '', '', ''),
(170, '“MAKTABGACHA VA BOSHLANG‘ICh TA’LIM” TIZIMIDA  YAPONIYA TAJRIBALARI O‘RGANILDI', 'maktabgacha-va-boshlangich-talim-tizimida-yaponiya-tajribalari-organildi', '<p>Toshkent viloyati Chirchik davlat pedagogika instituti “Maktabgacha va boshlang‘ich ta’lim\"</p>', '<p style=\"text-align: justify;\" align=\"center\">Toshkent viloyati Chirchik davlat pedagogika instituti “Maktabgacha va boshlang‘ich ta’lim” fakulteti  professor-o‘qituvchilari va talabalari ishtirokida Yaponiya sport universiteti  o‘qituvchisi Chinasu Mazaki bilan  uchrashuv bo‘lib o‘tdi. Ch. Mazaki auditoriyalarga kirib talabalar o‘qishi, intilishlari, qiziqishlari bilan yaqindan tanishdi. So‘ngra, mehmon institutmizning asosiy  binosida tashkil etilgan “Besh tashabbus” markazi bilan tanishib,  yaratilgan sharoitlar talabalarning bilim olishi va o‘z sohasini yetuk mutaxassasilari bo‘lishini  ta’kidlab o‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">Chinasu Mazaki “Besh tashabbus” markazi tashkil etilgan to‘garaklarning informatika, basketbol sport to‘garaklari faoliyati bilan yaqindan tanishdi.  Ch. Mazaki institutda tashkil etilgan “Qatag‘onlar muzeyi”dagi  tarixiy yodgorliklar, muzeydagi eksponatlarni ko‘zdan kechirar ekan,  O‘zbekistonning boy tarixiga ko‘pdan buyon qiziqishini aytib o‘tdi.  </p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">Ch. Mazaki “Maktabgacha ta’lim” yo‘nalishi  1-bosqich 19/4 guruhi  talabalariga jismoniy tarbiya va sport fanidan “Mahorat dars”i o‘tib berdi. Darsda maktabgacha yoshdagi bolalar bilan o‘tkaziladigan ertalabki badantarbiya mashqlarining noan’anaviy usullarini o‘rgatdi. “Mahorat darsi”dan so‘ng fakultet talabalari bilan qiziqarli savol-javoblar bo‘lib o‘tdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Uchrashuv barcha ishtirokchilarda yaxshi taassurot qoldirdi. </p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/maktabgacha-yaponiya-2.jpg\" class=\"fr-fic fr-dii\" style=\"width: 80%;\"></p>\r\n\r\n<p style=\"text-align: center;\"> </p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/maktabgacha-yaponiya-4.jpg\" class=\"fr-fic fr-dii\" style=\"width: 80%;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/maktabgacha-yaponiya-3.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>', '2019-10-28 06:10:44', '1', 2861, '2019-10-28 06:10:44', '2021-01-06 11:46:40', '/2019- yilgi/10.2019/maktabgacha-yaponiya-1.jpg', 1, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(171, '“O‘ZBEKISTON RESPUBLIKASI OLIY TA’LIM TIZIMINI 2030 YILGACHA RIVOJLANTIRISH KONSEPSIYASI” MUHOKAMADA...', 'ozbekiston-respublikasi-oliy-talim-tizimini-2030-yilgacha-rivojlantirish-konsepsiyasi-muhokamada', '<p>&nbsp; &nbsp; &nbsp;Kuni kecha institutning Tarix va tillar fakulteti &nbsp;“Ijtimoiy &nbsp; fanlar” kafedrasi tomonidan</p>', '<p style=\"text-align: left;\" align=\"center\">       Kuni kecha institutning Tarix va tillar fakulteti  “Ijtimoiy   fanlar” kafedrasi tomonidan <strong><em>“Toshkent viloyati Chirchiq davlat pedagogika institutining mahalliy va xalqaro reytingini oshirishda ilmiy-tadqiqot faoliyatining ahamiyati va uning samaradorligini oshirish yo‘llari”</em></strong> mavzusida ilmiy-amaliy seminar o‘tkazildi.</p>\r\n\r\n<p style=\"text-align: left;\">     Seminar “Ijtimoiy fanlar” kafedrasi o‘qituvchisi, tarix fanlari nomzodi, dotsent – <strong>Oybek Ostonov</strong> tomonidan olib borildi. O‘zbekiston Respublikasi Prezidentining 2019 yil 8-oktabrdagi “O‘zbekiston Respublikasi oliy ta’lim tizimini 2030 yilgacha rivojlantirish konsepsiyasi” to‘g‘risidagi farmoni asosida tashkil etilgan mazkur seminarda fakultet rahbariyati va professor-o‘qituvchilari ishtirok etishdi.</p>\r\n\r\n<p style=\"text-align: left;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/ozbekistonni-rivojlantirish-konsepsiyasi-3.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: left;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/ozbekistonni-rivojlantirish-konsepsiyasi-2.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/ozbekistonni-rivojlantirish-konsepsiyasi-5.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/ozbekistonni-rivojlantirish-konsepsiyasi-1.jpg\" class=\"fr-fic fr-dii\" style=\"width: 80%;\"></p>', '2019-10-28 06:18:42', '1', 3612, '2019-10-28 06:18:29', '2021-01-06 16:18:03', '/2019- yilgi/10.2019/ozbekistonni-rivojlantirish-konsepsiyasi-4.jpg', 1, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(172, '“TALANT SHOU” INSTITUTDA ISTE’DODLI TALABALAR KO‘P EKANLIGINI ISBOTLADI!', 'talant-shou-institutda-istedodli-talabalar-kop-ekanligini-isbotladi', '<p>Talabalarning san’atga muhabbat tuyg‘usini mustahkamlash, ularning san’atdagi</p>', '<p align=\"center\" style=\"text-align: justify;\">Talabalarning san’atga muhabbat tuyg‘usini mustahkamlash, ularning san’atdagi iste’dodini ro‘yobga chiqarish uchun sharoit yaratish va qo‘llab-quvvatlash maqsadida joriy yil 30 oktabr kuni Institutning “5 tashabbus Markazi” tomonidan “1-tashabbus haftaligi” doirasida “Talant shou” tanlovining final bosqichi o‘tkazildi. Tanlovga tayyorgarlik ishlari 2019 yil 16 sentabrdan boshlanib, talabalar san’atning “Qo‘shiqchilik”, “Milliy raqs”, “Badiiy o‘qish”, “Sozandalik” yo‘nalishlari bo‘yicha 3 bosqichdan iborat tartibda amalga oshirildi. Tanlovda ishtirok etish uchun murojaat qilgan 77 nafar talaba bilan mustaxassislar ishtirokida kasting o‘tkazildi. Jumladan, Tarix va tillar fakultetidan 17 nafar, Aniq fanlar fakultetidan 9 nafar, Tabiiy fanlar fakultetidan – 7 nafar, Boshlang‘ich va maktabgacha ta’lim fakultetidan - 17 nafar, Sport va chaqiriqqacha ta’lim fakultetidan - 14 nafar, Pedagogika fakultetidan - 13 nafar jami 77 nafar talabalar kastingda ishtirok etishdi. Tanlovning final bosqichiga 13 nafar talaba tavsiya etildi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/photo_2019-11-01_09-39-05.jpg\" class=\"fr-fic fr-dii\" style=\"width: 90%;\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">Chirchiq shahar Yoshlar Ittifoqi, Chirchiq shahar Madaniyat bo‘limi, “Manzur” xalq teatri mutaxassislaridan iborat Hakamlar hay’ati tomonidan qo‘yilgan ballar hamda &nbsp;330 nafar tomoshabin qo‘llab-quvvatlashliri asosida tanlov natijasiga ko‘ra Tarix va tillar fakulteti rus tili va adabiyoti ta’lim yo‘nalishi 1 kurs talabasi Gulina Omonova &nbsp;“Gran pri” g‘olibi deb e’lon qilindi. Tarix va tillar fakulteti Horijiy til va adabiyoti ta’lim yo‘nalishi 1 kurs talabasi Fozila Ashirmetova “Eng jo‘shqin qo‘shiq ijrochisi” nominatsiyasi, Tarix va tillar fakulteti Rus tili va adabiyoti ta’lim yo‘nalishi 1 kurs talabasi Boymetova Munisa “Eng ta’sircha monolog” nominatsiyasi, Boshlang‘ich va maktabgacha ta’lim fakulteti boshlang‘ich ta’lim yo‘nalishi 1 kurs talabasi Erkinova Navbaxor “Eng jozibali raqqosa” nominatsiyasi bo‘yicha diplom va esdalik sovg‘alariga sazovor bo‘ldi.</p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/photo_2019-11-01_09-39-17.jpg\" class=\"fr-fic fr-dii\" style=\"width: 90%;\"></p>\r\n\r\n<p style=\"text-align: justify;\">1-tashabbus doirasida tashkil etilgan mazkur “Talant shou” tanlovida “Ilm yurtim” deb nomlangan institut yoshlarining gimni ilk marta taqdim etildi. Gimn matni institutning Rus tili va adabiyoti ta’lim yo‘nalishi 2 kurs talabasi Bo‘riboeva Ismigul tomonidan yaratildi. Gimnga institutning Musiqa va defektologiya kafedrasi kabinet mudiri Sherzod Yaxanov musiqa yozdi. Finalning yakunida sahnada “Talant shou” tanlovining tashkilotchilari &nbsp;(“5 tashabbus Markazi” rahbari Sayyora Yaxshimbekova, Horijiy tillar kafedrasi o‘qituvchisi Doston Sherov, Rus tili va adabiyoti ta’lim yo‘nalishi talabasi Hojiakbar Hojimurodov, Musiqa ta’limi yo‘nalishi 2-kurs talabasi Ulug‘bek Jalolov) hamda 13 nafar final ishtirokchilari tomoshabinlar jo‘rligida mazkur qo‘shiqni birgalikda kuylashdi.</p>\r\n\r\n<p style=\"text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/10.2019/photo_2019-11-01_09-38-48.jpg\" class=\"fr-fic fr-dii\" style=\"width: 90%;\"></p>\r\n\r\n<p style=\"text-align: justify;\">Ushbu tanlov talabalarda san’at vositasida mamlakat taraqqiyoti yo‘lida birlashish, hamkorlikda “Milliy tiklanishdan – milliy yuksalish” sari dadil qadam qo‘yish hamda milliy san’atimizni rivojlantirish, iste’dodlarni ro‘yobga chiqarish uchun tarbiyaviy poydevor vazifasini bajardi.</p>\r\n\r\n<p style=\"text-align: center;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\">\r\n	<br>\r\n</p>', '2019-11-01 00:56:32', '1', 3578, '2019-11-01 00:52:48', '2021-01-07 01:36:07', '/2019- yilgi/10.2019/photo_2019-11-01_09-39-12.jpg', 2, NULL, 6, 0, '', 1, NULL, '', '', '', ''),
(173, 'KORRUPSIYAGA QARSHI KURASHISH BARCHANING BURCHI', 'korrupsiyaga-qarshi-kurashish-barchaning-burchi', '<p><strong>Bugungi kunda korrupsiyaga qarshi kurashish bo‘yicha olib borilayotgan islohotlar</strong></p>', '<p style=\"text-align: justify;\"><strong>Bugungi kunda korrupsiyaga qarshi kurashish bo‘yicha olib borilayotgan islohotlar va qabul qilinayotgan qonunlar kuchli fuqarolik jamiyati va huquqiy demokratik davlat qurish uchun zamin vazifasini o‘tamoqda. Bunday jamiyatni, davlatni qurish uchun fuqarolarning hamda har qanday tashkilotlar va ularda faoliyat yuritayotgan mansabdor shaxslarning qonun oldida tengligini, ya’ni faqatgina qonun ustuvorligini ta’minlash orqali erishish mumkinligi hech kimga sir emas.</strong></p>\r\n\r\n<p style=\"text-align: justify;\">Yer yuzidagi barcha davlatlarning ijtimoiy-iqtisodiy rivojiga, insoniyatning nafaqat ma’nan, balki jismonan yetuk bo‘lishiga ham asosiy to‘siq bo‘ladigan illatlardan biri – bu korrupsiyadir.</p>\r\n\r\n<p style=\"text-align: justify;\">Ayniqsa, vaqt o‘tgan sayin korrupsiyani tizimga tushishi bugungi kunda oldimizda turgan muhim vazifalardan biri respublikamizni xorijiy davlatlar tomonidan munosib e’tirof etilishiga, ijtimoiy-iqtisodiy rivojiga va yoshlarimizni o‘z tanlagan kasblari bo‘yicha yuksak bilimli yetuk mutaxassislar bo‘lib yetishishlariga juda katta salbiy ta’sirini bermasdan qolmaydi.</p>\r\n\r\n<p style=\"text-align: justify;\">Oliy ta’lim bo‘yicha bo‘lib o‘tgan bugungi navbatdan tashqari Hay’at yig‘ilishi korrupsiyaga qarshi kurashish barchaning birdek vazifasi ekanligi bo‘yicha ham o‘z ahamiyatiga ega deb hisoblayman. Toshkent davlat iqtisodiyot universitetida bo‘lib o‘tgan salbiy holatlar, hodisalar va ko‘rilgan keskin choralar barcha oliy ta’lim muassasalari rahbarlarini hamda professor-o‘qituvchilarini hushyor tortishga undaydi.</p>\r\n\r\n<p style=\"text-align: justify;\">Manba: <a href=\"http://edu.uz/uz/pages/axb\" target=\"_blank\">Axborot xizmati</a></p>\r\n\r\n<p>\r\n	<br>\r\n</p>', '2019-11-06 04:15:16', '1', 2822, '2019-11-06 04:15:16', '2020-12-25 17:02:10', '/6de9b8a1-1574-2a85-791a-12b294ea7697.jpg', 1, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(174, 'INSTITUTDA YOSHLAR YETAKCHISI SAYLANDI', 'institutida-yoshlar-yetakchisi-saylandi', '<p>Toshkent viloyati Chirchiq davlat pedagogika institutida institut Yoshlar ittifoqi</p>', '<p>Toshkent viloyati Chirchiq davlat pedagogika institutida institut Yoshlar ittifoqi yetakchisining hisobot-saylov yig‘ilishi &nbsp;bo‘lib o‘tdi. Yig‘ilishda institut yetakchisi Maftuna Qurbonovaning o‘tgan o‘quv yilidagi institutda Yoshlar ittifoqining faoliyati yuzasidan axboroti tinglandi.</p>\r\n\r\n<p style=\"text-align: justify;\">Kun tartibidagi ikkinchi masala yuzasidan &nbsp;institut yoshlar ittifoqi yetakchisini saylash jarayoni bo‘lib o‘tdi. Unga ko‘ra, 3 nafar nomzod o‘z dasturlari, rejalari bilan nomzodligini ko‘rsatishdi.</p>\r\n\r\n<p style=\"text-align: justify;\">Yakuniy saylov natijalariga ko‘ra, Toshkent viloyati Chirchiq davlat pedagogika instituti “Tabiiy fanlar” fakultetida faoliyat olib borayotgan, institut yoshlar yetakchiligiga nomzod – Elyor Saydaliev institut Yoshlar yetakchisi lavozimiga saylandi.</p>\r\n\r\n<p style=\"text-align: justify;\">Elyor Saydaliev – 1988 yilda Xorazm viloyati Shovot tumanida tug‘ilgan. Oilali. 2 nafar farzandi bor. Chirchiq Oliy tank qo‘mondonlik-muhandislik &nbsp;bilim yurtida tahsil olgan.</p>\r\n\r\n<p style=\"text-align: justify;\">2010 yilda Chirchiq Oliy tank qo‘mondonlik-muhandislik &nbsp;bilim yurtini tamomlab, O‘zbekiston Respublikasi Qurolli kuchlari safida shartnoma asosida harbiy xizmatni o‘tagan.</p>\r\n\r\n<p style=\"text-align: justify;\">2019 yil 29 oktabrdan Toshkent viloyati Chirchiq davlat pedagogika instituti Yoshlar yetakchisi lavozimiga saylandi.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-04_11-44-48.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-04_11-43-47.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-10-29_17-10-20.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: center;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-04_11-44-16.jpg\" style=\"width: 80%;\" class=\"fr-fic fr-dib\"></p>', '2019-11-07 01:01:24', '1', 2005, '2019-11-07 01:01:24', '2021-01-04 06:48:50', '/2019- yilgi/11.2019/photo_2019-11-04_11-44-11.jpg', 1, NULL, 25, 0, '', 1, NULL, '', '', '', ''),
(175, 'BOSHLANG‘ICH TA’LIM VA SPORT TARBIYAVIY ISHI YO‘NALISHI TALABALARI PEDAGOGIK AMALIYOT O‘TASHDI', 'boshlangich-talim-va-sport-tarbiyaviy-ishi-yonalishi-talabalari-pedagogik-amaliyot-otashdi', '<p>Toshkent viloyati Chirchiq davlat pedagogika instituti Maktabgacha</p>', '<p align=\"center\" style=\"text-align: justify;\">&nbsp; &nbsp; Students of the direction of primary education and sport educational work of the faculty of faculty of “Preschool and primary education” of &nbsp;Chirchik state pedagogical institute of Tashkent region passed pedagogical practice since September 30, 2019 to October 26 at the general education schools # 2-7-25.</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/boshlan_maktab_ochiq_dars_5.jpg\" class=\"fr-fic fr-dii\" style=\"width: 95%;\">\r\n				<br>\r\n			</td>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/boshlan_maktab_ochiq_dars_3.jpg\" style=\"width: 60%;\" class=\"fr-fic fr-dib\">\r\n				<br>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>During the process of practice students closely introduced with the school activity, working documents. They consolidated the theoretical knowledge in the practice work. The process of the practice was proved once more the necessity of conducting lessons organized in primary classes on the base of handouts of new innovation technologies.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/boshlan_maktab_ochiq_dars_4.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">During the passed time each student conducted “Open class lessons” in the assign classes together with the &nbsp;form masters. &nbsp;</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-11-09 05:03:59', '1', 2125, '2019-11-09 05:03:59', '2021-01-07 03:33:14', '/2019- yilgi/11.2019/boshlan_maktab_ochiq_dars_2.jpg', 2, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(176, '“BESHTA MUHIM TASHABBUS” – ERTAMIZ EGALARI ISTIQBOLI YO\'LIDA', 'beshta-muhim-tashabbus-ertamiz-egalari-istiqboli-yolida', '<p>O\'zbekiston Respublikasi Prezidentining Yoshlar</p>', '<p align=\"center\" style=\"text-align: justify;\">O\'zbekiston Respublikasi Prezidentining Yoshlar ma\'naviyatini yuksaltirish va ularning bo\'sh vaqtlarini mazmunli tashkil etish bo\'yicha 5 muhim tashabbusning 4-tashabbusi \"Kitobxonlikni keng targ\'ib qilish\" yo’nalishida Maktabgacha va boshlanğich ta\'lim fakulteti, \"Boshlang\'ich ta\'lim va sport tarbiyaviy ishi\" yo\'nalishi 17/1 guruh talabalari tomonidan <strong>\"Kitob – tafakkur xazinasi\"</strong> shiori ostida ma’naviy-ma’rifiy tadbir o\'tkazildi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/beshta-muhim-tashabbus-ertamiz-egalari-istiqboli-yolida.png\" class=\"fr-fic fr-dii\" style=\"width: 90%;\">\r\n				<br>\r\n			</td>\r\n			<td style=\"width: 50%; text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/beshta-muhim-tashabbus-ertamiz-egalari-istiqboli-yolida-5.jpg\" class=\"fr-fic fr-dii\" style=\"width: 90%;\">\r\n				<br>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\"> Tadbirda fakultetning barcha professor-o’qituvchilari, talaba-yoshlar ishtirok etishdi. Tadbir davomida  talabalar tomonidan sahnalashtirilgan O`tkir Hoshimovning “Dunyoning ishlari”, Abdulla Qodiriyning “O`tkan kunlar” “Mehrobdan chayon” romanlaridan kichik sahna ko`rinishlari namoyish etildi. “Maftuningman” kinofilmi asnosida tayyorlangan “Do’ppi tikdim” raqsi mahorat bilan ijro etildi. Tadbir tomoshabinlarining kitobxonliklari ham sinovdan o’tkazilib, savol-javoblarda faol ishtirok etgan talabalarga kitob sovg`a qilindi. Tadbir so`ngida, 17/1 guruh talabalari tomonidan Zulfiya Mo`minovaning <strong>“Bolaga kitob bering”</strong>  she’ri  o`qildi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/beshta-muhim-tashabbus-ertamiz-egalari-istiqboli-yolida-4.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></td>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/beshta-muhim-tashabbus-ertamiz-egalari-istiqboli-yolidapng.jpg\" style=\"width: 90%;\" class=\"fr-fic fr-dib\"></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\"><strong><em>      Tadbir barchada katta taassurot qoldirdi.</em></strong></p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-11-09 05:10:49', '1', 2582, '2019-11-09 05:10:49', '2021-01-07 00:42:38', '/2019- yilgi/11.2019/beshta-muhim-tashabbus-ertamiz-egalari-istiqboli-yolida-2.jpg', 2, NULL, 6, 0, '', 1, NULL, '', '', '', ''),
(178, '“PEDAGOGIK TA’LIM INNOVATSION KLASTERI” ASOSIDA HAMKORLIK', 'pedagogik-talim-innovatsion-klasteri-asosida-hamkorlik', NULL, '<p>         Toshkent viloyati Chirchiq davlat pedagogika institutida viloyat Xalq ta’limi boshqarmasi, xalq ta’limi xodimlarini qayta tayyorlash va ularning malakasini oshirish hududiy markazi Toshkent viloyati umumta’lim maktablari reytingini yangi bosqichga olib chiqish maqsadida “Yo‘l xarita”si ishlab chiqishga mo‘ljallangan uchrashuv bo‘lib o‘tdi.</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50.0000%;\">\r\n				<div style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/IMG_1469-min.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></div>\r\n			</td>\r\n			<td style=\"width: 50.0000%;\">\r\n				<div style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/IMG_1360-min.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p>       Uchrashuv davomida mutasaddilar institutdagi amaliy hamda nazariy dars jarayonlari, “5 muhim tashabbus Markazi” hamda institut qoshida tashkil etilgan “Qatag‘on qurbonlari” muzeyi faoliyati bilan tanishishdi.</p>\r\n\r\n<p> “Maktab laborotoriyasi” faoliyati yo‘lga qo‘yilgan umumta’lim maktablaridagi dars mashg‘ulotlarida ishtirok etishdi.</p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/IMG_1396-min.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></td>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/IMG_6191-min.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p>        Uchrashuv so‘ngida,  <strong>“Pedagogik ta’lim innovatsion klasteri” asosida hamkorlikni yo‘lga qo‘yish hamda rivojlantirish bo‘yicha </strong>Qo‘shma Qarorni imzolash marosimi bo‘lib o‘tdi. Qarorda  Toshkent viloyatidagi umumta’lim maktablarida mavjud bo‘sh ish o‘rinlari to‘g‘risida ma’lumotlar bazasini shakllantirish, talabalar amaliyotini o‘tkazish bo‘yicha yetakchi umumta’lim maktablari bilan shartnomalar imzolash, Chirchiq shahrida <strong>“Maktab-laboratoriya”larini tashkil etish,</strong> talabalar amaliyotini samarali tashkil etish, bitiruvchi talabalarni ishga joylashishlariga erishish kabi muhim vazifalar belgilangan.</p>\r\n\r\n<p><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/IMG_1436-min.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\">\r\n	<br>\r\n</p>\r\n\r\n<p>         “Qo‘shma qaror”da belgilangan vazifalar bugungi kunda o‘ta dolzarb hisoblanib, kelgusida viloyatdagi ta’lim reytingining o‘sishiga olib kelishi kutilmoqda...</p>\r\n\r\n<h4>\r\n	<br>\r\n</h4>\r\n\r\n<p style=\"text-align: right;\"><strong>Institut, viloyat XTB  axborot xizmatlari. </strong></p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<h4> </h4>\r\n\r\n<h4> </h4>\r\n\r\n<h4> </h4>\r\n\r\n<h4><strong> </strong></h4>\r\n\r\n<p><strong> </strong></p>\r\n\r\n<p><strong> </strong></p>\r\n\r\n<p><strong> </strong></p>\r\n\r\n<p> </p>', '2019-11-14 05:41:42', '1', 1755, '2019-11-14 05:29:46', '2021-01-04 21:10:49', '/2019- yilgi/11.2019/IMG_6457-min.jpg', 1, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(179, 'QIBRAY TUMANIDA KLASTER METODI ASOSIDA OCHIQ DARS O‘TKAZILDI!', 'qibray-tumanida-klaster-metodi-asosida-ochiq-dars-otkazildi', NULL, '<p align=\"center\" style=\"text-align: justify;\">Joriy yilning 14 noyabr kuni Toshkent viloyati Chirchiq davlat pedagogika instituti “Aniq fanlar” fakulteti “Informatika” kafedrasi katta o‘qituvchisi Adham Boymurodov va shu yo‘nalish talabalari  Qibray tumanidagi 15-sonli umumiy o‘rta ta’lim maktabining 6-sinf o‘quvchilari uchun  “Xujjatlarni tahrirlash” mavzusida ochiq dars tashkil etishdi. </p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-15_15-40-04.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">Dars yangi pedagogik texnologiyalar asosida klaster metodidan foydalanilgan holda olib borildi.  Dars mashg‘ulotidan so‘ng, muassasadagi informatika fani o‘qituvchilari va talabalar o‘rtasida ochiq dars muhokamasi bo‘lib o‘tdi. Muhokamada tajribali o‘qituvchilar talabalarning mashg‘ulot davomidagi yutuq va kamchiliklarini  ko‘rsatib o‘tishdi.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-11-15 05:41:47', '1', 2218, '2019-11-15 05:41:01', '2021-01-06 18:57:48', '/2019- yilgi/11.2019/photo_2019-11-15_15-40-09.jpg', 2, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(180, 'SERGEY YESENIN MUZEYIGA SAYOHAT', 'sergey-yesenin-muzeyiga-sayohat', NULL, '<p align=\"center\" style=\"text-align: justify;\">Rus tili va adabiyoti kafedrasi talabalari buyuk rus &nbsp;shoiri va tasavvurchisi Sergey Yesenin nomidagi muzeyga sayohat uyushtirishdi. Sayohat davomida talabalar shoirning hayot yo’li va ijodiy faoliyati bilan yaqindan tanishdilar. &nbsp;Talabalar shoirning O\'zbekiston shahriga 20 kunlik &nbsp;tashrifi uning ijodiy hayotida muhim bosqich bo\'lganligi, &nbsp;sharqning asl muhitini aks ettirgan &nbsp;\"Fors motivlari\" ni yaratgani kabi ko’plab qiziqarli ma’lumotlarga ega bo’lishdi. Mana shunday sayohatlarning tashkil etilishi talaba-yoshlarning o’rganayotgan bilimlarini yanada mustahkamlashga xizmat qiladi.</p>\r\n\r\n<p style=\"text-align: justify;\">Talabalar ushbu sayohatdan katta taassurotlar bilan qaytishdi.</p>', NULL, '3', 0, '2019-11-15 05:46:22', '2019-11-15 05:46:22', '', 2, NULL, 13, 0, NULL, 0, NULL, '', '', '', ''),
(181, 'ALEKSANDR ARKADIYEVICH FAYNBERG XOTIRASIGA EHTIROM', 'aleksandr-arkadiyevich-faynberg-xotirasiga-ehtirom', NULL, '<p align=\"center\" style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika institutida shoir <strong>Aleksandr Arkadiyevich Faynberg</strong> xotirasiga bag‘ishlangan adabiy kecha bo‘lib o‘tdi. </p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50.0000%;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-15_15-47-44.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></td>\r\n			<td style=\"width: 50.0000%;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-15_15-47-48.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">Tadbirda institut talabalari va o\'qituvchilari, \"Rus tili va adabiyoti\" yo\'nalishi talabalari  ishtirok etdilar. Kecha davomida talabalar shoirning hayoti va ijodi, mashhur she\'rlaridan namunalar keltirishdi. Sahna ko’rinishlari namoyish etishdi.</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-11-15 05:50:06', '1', 2027, '2019-11-15 05:50:06', '2021-01-06 11:56:39', '/2019- yilgi/11.2019/photo_2019-11-15_15-47-56.jpg', 2, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(182, 'TARIXIY QADAMJOLARGA SAYOHAT', 'tarixiy-qadamjolarga-sayohat', NULL, '<p align=\"center\" style=\"text-align: justify;\">Rus tili va adabiyoti kafedrasi talabalari  “Qatag’on qurbonlari xotirasi” muzeyi hamda buyuk rus  shoiri va tasavvurchisi Sergey Yesenin nomidagi uy-muzeyiga sayohat uyushtirishdi. </p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-18_16-07-38.jpg\" style=\"width: 85%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">Mustaqillik yo’lida jon fido qilgan ota-bobolarimizning yorqin xotirasini ziyorat qilishdi. Rus shoiri Sergey Yeseninning hayot yo’li va ijodiy faoliyati bilan yaqindan tanishdilar. </p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-18_16-07-34.jpg\" style=\"width: 85%;\" class=\"fr-fic fr-dib\"></td>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-15_15-42-30.jpg\" style=\"width: 85%;\" class=\"fr-fic fr-dib\"></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align: justify;\">Mana shunday sayohatlarning tashkil etilishi talaba-yoshlarning tarixiy bilimlarini yanada mustahkamlashga xizmat qiladi.</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-15_15-42-53.jpg\" style=\"width: 85%;\" class=\"fr-fic fr-dib\"></td>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-15_15-42-50.jpg\" style=\"width: 85%;\" class=\"fr-fic fr-dib\"></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<br>\r\n			</td>\r\n			<td>\r\n				<br>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p><em>Talabalar ushbu sayohatdan katta taassurotlar bilan qaytishdi.  </em></p>\r\n\r\n<p align=\"right\" style=\"text-align: right;\"><strong>Institut axborot xizmati.</strong></p>', '2019-11-18 06:11:20', '1', 1702, '2019-11-18 06:11:20', '2020-12-25 06:49:50', '/2019- yilgi/11.2019/photo_2019-11-18_16-07-38.jpg', 2, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(183, '“HILPIRA BAYROG‘IM, HILPIRA MAG‘RUR!”', 'ilpira-bajroim-ilpira-marur', NULL, '<p align=\"center\" style=\"text-align: justify;\"><strong>Davlat ramzlari hamisha muqaddas va ulug‘ sanaladi. Chunki u xalqning o‘tmishi, buguni va kelajagini o‘zida mujassam etgan Vatanning yorqin timsolidir.</strong></p>\r\n\r\n<p align=\"center\" style=\"text-align: center;\"><strong><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-19_15-06-27.jpg\" style=\"width: 60%;\" class=\"fr-fic fr-dib\"></strong>\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">Bayrog‘imiz o‘zida butun O‘zbekiston xalqining asriy orzu-istaklari, g‘ayrati va shijoatini, xalqimizga xos mehmondo‘stlik qadriyatlarini mujassam etgan. Davlat ramzlari har bir mamlakatning mustaqilligini ifodalovchi muqaddas belgidir.</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-19_15-06-18.jpg\" style=\"width: 60%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\">Davlatimiz bayrog‘i qabul qilinganligining 28 yilligi munosabati bilan Toshkent viloyati Chirchiq davlat pedagogika instituti Talabalar turar joyida <strong>“Doim yuksaklarda hilpira bayrog‘im”&nbsp;</strong>nomli bayram tadbiri o‘tkazildi.</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/photo_2019-11-19_15-06-24.jpg\" style=\"width: 60%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p>Tadbirda TTJ talabalari tomonidan bayrog‘imiz haqida ma’lumotlar keltirildi, ular ijrosida she’r, kuy-qo‘shig‘u diltortar raqslar namoyish etildi.</p>\r\n\r\n<p>\r\n	<br>\r\n</p>\r\n\r\n<p align=\"right\"><strong>Institut axborot xizmati.</strong></p>', '2019-11-19 05:08:11', '2', 363, '2019-11-19 05:08:11', '2019-11-23 02:24:46', '/2019- yilgi/11.2019/photo_2019-11-19_15-06-27.jpg', 2, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(184, 'BAYROG‘IM MENING - G‘URURIM MENING (video lavha)', 'bayrogim-mening-gururim-mening', NULL, '<p style=\"text-align: justify;\">«O‘zbekiston Respublikasi davlat bayrog‘i to‘g‘risida»gi qonun qabul qilingan kun munosabati bilan Chirchiq davlat pedagogika instituti iqtidorli talabalari tomonidan \"BAYROG‘IM MENING - G‘URURIM MENING\" shiori ostida tayyorlangan videorolik.</p>\r\n<hr>\r\n\r\n<p style=\"text-align: center;\">\r\n	<iframe width=\"100%\" height=\"640px\" src=\"https://www.youtube.com/embed/ZXYD-7h7QD0\" frameborder=\"0\" allowfullscreen=\"\"></iframe>\r\n</p>', '2019-11-21 04:03:25', '1', 2657, '2019-11-21 04:03:25', '2021-01-06 05:19:48', '/2019- yilgi/11.2019/m_bf6xJ1m9AUpWvZa1rMBautjN0vv75r.jpg', 1, NULL, 5, 0, '', 1, NULL, '', '', '', ''),
(185, 'O‘TMISHIMIZ, BUGUNIMIZ VA KELAJAGIMIZ RAMZI...', 'otmishimiz-bugunimiz-va-kelajagimiz-ramzi', NULL, '<p style=\"text-align: justify;\">Toshkent viloyati Chirchiq davlat pedagogika instituti Pedagogika fakulteti  professor-o‘qituvchilari va talaba-yoshlari O‘zbekiston Respublikasi davlat bayrog‘i qabul qilinganligining 28 yilligini munosib qarshi olishdi.</p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/333.jpg\" style=\"width: 60%;\" class=\"fr-fic fr-dib\"></p>\r\n\r\n<p style=\"text-align: justify;\"> Fakultetda muqaddas sana munosabati bilan <strong>“Bayrog‘imiz – g‘ururimiz, faxrimiz”</strong> mavzusida tadbir o‘tkazildi.</p>\r\n\r\n<table style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/444.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\">\r\n				<br>\r\n			</td>\r\n			<td style=\"width: 50%; text-align: center;\"><img src=\"https://www.cspi.uz/storage/app/media/2019-%20yilgi/11.2019/222.jpg\" style=\"width: 95%;\" class=\"fr-fic fr-dib\"></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"> Tadbirda talaba-yoshlar tomonidan tayyorlangan ona-yurt, hurlik, ozodlikni tarannum etgan kuy-qo‘shiqlar jarang sochdi.</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<br>\r\n</p>', '2019-11-21 06:37:08', '1', 2286, '2019-11-21 06:36:38', '2020-12-29 15:47:35', '/2019- yilgi/11.2019/111.jpg', 2, NULL, 6, 0, '', 1, NULL, '', '', '', '');
